mapper.update:
update projects set
name = #{name}, charge_user_id = #{chargeUserId}, code_line_number = #{codeLineNumber}, schedule = #{schedule}, function_points = #{functionPoints}, completed_function_points = #{completedFunctionPoints}, update_count = #{updateCount}, last_update_time = #{lastUpdateTime}, type = #{type}, characterization = #{characterization}, store_count = #{storeCount}, like_count = #{likeCount},
where id = #{id}
